// Dashboard.js COMPLETO

import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Button,
  TextField,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  CircularProgress,
  IconButton
} from "@material-ui/core";

import {
  Add as AddIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  PictureAsPdf as PdfIcon,
  GetApp as ExcelIcon,
  ExitToApp as LogoutIcon
} from "@material-ui/icons";

import "./Dashboard.css";

export default function Dashboard() {
  const API = "https://backtestmar.onrender.com";

  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);

  // Busca e paginação
  const [search, setSearch] = useState("");
  const [page, setPage] = useState(1);

  // Modal Add
  const [addOpen, setAddOpen] = useState(false);
  const [addData, setAddData] = useState({
    title: "",
    description: "",
    price: ""
  });
  const [addImage, setAddImage] = useState(null);
  const [addLoading, setAddLoading] = useState(false);

  // Modal Edit
  const [editOpen, setEditOpen] = useState(false);
  const [editData, setEditData] = useState({});
  const [editImage, setEditImage] = useState(null);
  const [editLoading, setEditLoading] = useState(false);

  // Carrega os dados
  const fetchData = async () => {
    setLoading(true);
    try {
      const res = await axios.get(`${API}/items?search=${search}&page=${page}`);
      setItems(res.data.items || []);
    } catch (error) {
      console.log("Erro ao buscar:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [search, page]);

  // ADD ITEM
  const addItem = async () => {
    if (!addData.title || !addData.price) return alert("Preencha os campos");

    setAddLoading(true);

    try {
      const formData = new FormData();
      formData.append("title", addData.title);
      formData.append("description", addData.description);
      formData.append("price", addData.price);
      if (addImage) formData.append("image", addImage);

      await axios.post(`${API}/items`, formData);

      setAddOpen(false);
      setAddData({ title: "", description: "", price: "" });
      setAddImage(null);
      fetchData();

    } catch (err) {
      alert("Erro ao adicionar item");
    } finally {
      setAddLoading(false);
    }
  };

  // EDIT ITEM
  const openEdit = (item) => {
    setEditData(item);
    setEditImage(null);
    setEditOpen(true);
  };

  const updateItem = async () => {
    setEditLoading(true);

    try {
      const formData = new FormData();
      formData.append("title", editData.title);
      formData.append("description", editData.description);
      formData.append("price", editData.price);

      if (editImage) formData.append("image", editImage);

      await axios.put(`${API}/items/${editData._id}`, formData);

      setEditOpen(false);
      fetchData();

    } catch (err) {
      alert("Erro ao atualizar item");
    } finally {
      setEditLoading(false);
    }
  };

  // DELETE ITEM
  const deleteItem = async (id) => {
    if (!window.confirm("Excluir este item?")) return;

    try {
      await axios.delete(`${API}/items/${id}`);
      fetchData();
    } catch (err) {
      alert("Erro ao excluir");
    }
  };

  // PDF
  const generatePDF = async () => {
    window.open(`${API}/excel/pdf`, "_blank");
  };

  // EXCEL
  const generateExcel = async () => {
    window.open(`${API}/excel/export`, "_blank");
  };

  // LOGOUT
  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user_id");
    window.location.href = "/";
  };

  return (
    <div className="dashboard-container">

      {/* TOPO */}
      <div className="top-bar">
        <h2>Dashboard</h2>

        <div>
          <IconButton color="primary" onClick={() => setAddOpen(true)}>
            <AddIcon />
          </IconButton>

          <IconButton color="primary" onClick={generatePDF}>
            <PdfIcon />
          </IconButton>

          <IconButton color="primary" onClick={generateExcel}>
            <ExcelIcon />
          </IconButton>

          <IconButton color="secondary" onClick={logout}>
            <LogoutIcon />
          </IconButton>
        </div>
      </div>

      {/* SEARCH */}
      <TextField
        label="Buscar..."
        fullWidth
        variant="outlined"
        className="search-box"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      {/* LOADING */}
      {loading && (
        <div className="loading-container">
          <CircularProgress />
        </div>
      )}

      {/* LISTA */}
      <div className="card-list">
        {items.map((item) => (
          <div key={item._id} className="product-card">
            <img src={item.imageUrl} alt="" className="card-img" />

            <h3>{item.title}</h3>
            <p>{item.description}</p>
            <strong>R$ {item.price}</strong>

            <div className="card-actions">
              <IconButton color="primary" onClick={() => openEdit(item)}>
                <EditIcon />
              </IconButton>

              <IconButton color="secondary" onClick={() => deleteItem(item._id)}>
                <DeleteIcon />
              </IconButton>
            </div>
          </div>
        ))}
      </div>

      {/* PAGINAÇÃO */}
      <div className="pagination-area">
        <Button onClick={() => setPage(page - 1)} disabled={page === 1}>
          Anterior
        </Button>

        <span style={{ margin: "0 10px" }}>Página {page}</span>

        <Button onClick={() => setPage(page + 1)}>
          Próxima
        </Button>
      </div>

      {/* MODAL ADD */}
      <Dialog open={addOpen} onClose={() => setAddOpen(false)}>
        <DialogTitle>Adicionar Produto</DialogTitle>

        <DialogContent className="dialog-content">
          <TextField
            label="Título"
            fullWidth
            margin="dense"
            value={addData.title}
            onChange={(e) => setAddData({ ...addData, title: e.target.value })}
          />

          <TextField
            label="Descrição"
            fullWidth
            margin="dense"
            value={addData.description}
            onChange={(e) =>
              setAddData({ ...addData, description: e.target.value })
            }
          />

          <TextField
            label="Preço"
            fullWidth
            margin="dense"
            type="number"
            value={addData.price}
            onChange={(e) =>
              setAddData({ ...addData, price: e.target.value })
            }
          />

          <div className="file-input-row">
            <input
              type="file"
              onChange={(e) => setAddImage(e.target.files[0])}
            />
          </div>
        </DialogContent>

        <DialogActions>
          <Button onClick={() => setAddOpen(false)} color="secondary">
            Cancelar
          </Button>

          <Button
            onClick={addItem}
            color="primary"
            variant="contained"
            disabled={addLoading}
          >
            {addLoading ? "Salvando..." : "Adicionar"}
          </Button>
        </DialogActions>
      </Dialog>

      {/* MODAL EDIT */}
      <Dialog open={editOpen} onClose={() => setEditOpen(false)}>
        <DialogTitle>Editar Produto</DialogTitle>

        <DialogContent className="dialog-content">
          <TextField
            label="Título"
            fullWidth
            margin="dense"
            value={editData.title}
            onChange={(e) =>
              setEditData({ ...editData, title: e.target.value })
            }
          />

          <TextField
            label="Descrição"
            fullWidth
            margin="dense"
            value={editData.description}
            onChange={(e) =>
              setEditData({ ...editData, description: e.target.value })
            }
          />

          <TextField
            label="Preço"
            fullWidth
            margin="dense"
            type="number"
            value={editData.price}
            onChange={(e) =>
              setEditData({ ...editData, price: e.target.value })
            }
          />

          {editData.imageUrl && (
            <div className="preview-img-container">
              <p>Imagem atual:</p>
              <img src={editData.imageUrl} alt="" className="preview-img" />
            </div>
          )}

          <div className="file-input-row">
            <input
              type="file"
              onChange={(e) => setEditImage(e.target.files[0])}
            />
          </div>
        </DialogContent>

        <DialogActions>
          <Button onClick={() => setEditOpen(false)} color="secondary">
            Cancelar
          </Button>

          <Button
            onClick={updateItem}
            color="primary"
            variant="contained"
            disabled={editLoading}
          >
            {editLoading ? "Salvando..." : "Salvar alterações"}
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
