import styled from "styled-components";

export const Container = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  background-color: #414040;
  padding: 1rem;
  color: #fff;
`;

export const Social = styled.div`
  a {
    font-size: 3.5rem;
    color: #fff;
    text-decoration: none;
  }
`;
