import {useState, useEffect} from 'react';
import Player from '../Player/Player';
import React from 'react';
import "bootstrap/dist/css/bootstrap.min.css";

function App() {
  const [songs] = useState([
    {
      title: "Forget me too ft. Halsey",
      artist: "Machine Gun Kelly",
      img_src: "./images/c0.jpg",
      src: "./music/audio.mp3"
    },
    {
      title: "Song 2",
      artist: "Artist 2",
      img_src: "./images/img.jpeg",
      src: "./music/audio1.mp3"
    },
    {
      title: "Song 3",
      artist: "Artist 3",
      img_src: "./images/c0.jpg",
      src: "./music/audio.mp3"
    },
    {
      title: "Song 4",
      artist: "Artist 4",
      img_src: "./images/img.jpeg",
      src: "./music/audio1.mp3"
    }
  ]);

  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const [nextSongIndex, setNextSongIndex] = useState(0);

  useEffect(() => {
    setNextSongIndex(() => {
      if (currentSongIndex + 1 > songs.length - 1) {
        return 0;
      } else {
        return currentSongIndex + 1;
      }
    });
  }, [currentSongIndex]);

  return (
    <div className="container">
    <div className="card mt-6 bg-warning">
      <div className="card-title">
        <div className="row">
          <div className="col">
            <br></br>
            <h3 className="mx-3 my-3 text-center">BIOGRAFIA DA REDE DE CASAIS</h3>
            <h3 className="mx-3 my-3 text-center">AMAI SEMPRE</h3>
          </div>

          </div>



      <Player 
        currentSongIndex={currentSongIndex} 
        setCurrentSongIndex={setCurrentSongIndex} 
        nextSongIndex={nextSongIndex} 
        songs={songs}
      />
    </div>
    </div>
    </div>
  );
}

export default App;
